import React, { useState } from 'react';
import { useModal } from '../context/ModalContext';

const Login = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const { success, error } = useModal();

  const handleInputChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const validateForm = () => {
    if (!formData.email || !formData.password) {
      error('Please fill in all required fields');
      return false;
    }

    if (!formData.email.includes('@')) {
      error('Please enter a valid email address');
      return false;
    }

    if (formData.password.length < 6) {
      error('Password must be at least 6 characters long');
      return false;
    }

    if (!isLogin) {
      if (!formData.name) {
        error('Please enter your name');
        return false;
      }
      if (formData.password !== formData.confirmPassword) {
        error('Passwords do not match');
        return false;
      }
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setIsLoading(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      if (isLogin) {
        // Check if user exists in localStorage
        const existingUsers = JSON.parse(localStorage.getItem('nailPolishPickerUsers') || '[]');
        const user = existingUsers.find(u => u.email === formData.email && u.password === formData.password);
        
        if (user) {
          success('Login successful!');
          onLogin({
            email: user.email,
            name: user.name,
            id: user.id
          });
        } else {
          error('Invalid email or password. Please check your credentials or sign up for a new account.');
          setIsLoading(false);
          return;
        }
      } else {
        // Sign up - check if user already exists
        const existingUsers = JSON.parse(localStorage.getItem('nailPolishPickerUsers') || '[]');
        const userExists = existingUsers.find(u => u.email === formData.email);
        
        if (userExists) {
          error('An account with this email already exists. Please sign in instead.');
          setIsLoading(false);
          return;
        }
        
        // Create new user
        const newUser = {
          email: formData.email,
          name: formData.name,
          password: formData.password,
          id: Date.now().toString()
        };
        
        existingUsers.push(newUser);
        localStorage.setItem('nailPolishPickerUsers', JSON.stringify(existingUsers));
        
        success('Account created successfully!');
        onLogin({
          email: newUser.email,
          name: newUser.name,
          id: newUser.id
        });
      }
    } catch (err) {
      error('Something went wrong. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setFormData({
      email: '',
      password: '',
      confirmPassword: '',
      name: ''
    });
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h1>Nail Polish Picker</h1>
          <p>Let me help you choose the perfect nail polish!</p>
        </div>
        
        <div className="login-form-container">
          <h2>{isLogin ? 'Welcome Back!' : 'Create Account'}</h2>
          <p className="login-subtitle">
            {isLogin 
              ? 'Sign in to access your nail polish collection' 
              : 'Join us to save your collection across devices'
            }
          </p>

          <form onSubmit={handleSubmit} className="login-form">
            {!isLogin && (
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Enter your full name"
                  required={!isLogin}
                />
              </div>
            )}

            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="Enter your password"
                required
              />
            </div>

            {!isLogin && (
              <div className="form-group">
                <label htmlFor="confirmPassword">Confirm Password</label>
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  placeholder="Confirm your password"
                  required={!isLogin}
                />
              </div>
            )}

            <button 
              type="submit" 
              className="login-button"
              disabled={isLoading}
            >
              {isLoading 
                ? (isLogin ? 'Signing In...' : 'Creating Account...') 
                : (isLogin ? 'Sign In' : 'Create Account')
              }
            </button>
          </form>

          <div className="login-toggle">
            <p>
              {isLogin ? "Don't have an account? " : "Already have an account? "}
              <button 
                type="button" 
                className="toggle-button"
                onClick={toggleMode}
              >
                {isLogin ? 'Sign Up' : 'Sign In'}
              </button>
            </p>
          </div>

          <div className="demo-note">
            <p><strong>Demo Mode:</strong> This is a demonstration. In a real app, this would connect to a backend service for authentication and data storage.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
