import React, { createContext, useContext, useReducer, useEffect } from 'react';

const AuthContext = createContext();

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        user: action.payload,
        isAuthenticated: true,
        isLoading: false
      };
    case 'LOGOUT':
      return {
        ...state,
        user: null,
        isAuthenticated: false,
        isLoading: false
      };
    case 'SET_LOADING':
      return {
        ...state,
        isLoading: action.payload
      };
    case 'RESTORE_SESSION':
      return {
        ...state,
        user: action.payload,
        isAuthenticated: !!action.payload,
        isLoading: false
      };
    default:
      return state;
  }
};

const initialState = {
  user: null,
  isAuthenticated: false,
  isLoading: true
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // Restore session on app load
  useEffect(() => {
    const savedUser = localStorage.getItem('nailPolishPickerUser');
    if (savedUser) {
      try {
        const user = JSON.parse(savedUser);
        dispatch({ type: 'RESTORE_SESSION', payload: user });
      } catch (error) {
        console.error('Error restoring session:', error);
        localStorage.removeItem('nailPolishPickerUser');
        dispatch({ type: 'RESTORE_SESSION', payload: null });
      }
    } else {
      dispatch({ type: 'RESTORE_SESSION', payload: null });
    }
  }, []);

  const login = (userData) => {
    // Save user data to localStorage for persistence
    localStorage.setItem('nailPolishPickerUser', JSON.stringify(userData));
    dispatch({ type: 'LOGIN', payload: userData });
  };

  const logout = () => {
    // Clear user data from localStorage
    localStorage.removeItem('nailPolishPickerUser');
    localStorage.removeItem('nailPolishPickerData'); // Clear app data too
    dispatch({ type: 'LOGOUT' });
  };

  const updateUser = (updatedUserData) => {
    const newUserData = { ...state.user, ...updatedUserData };
    localStorage.setItem('nailPolishPickerUser', JSON.stringify(newUserData));
    dispatch({ type: 'LOGIN', payload: newUserData });
  };

  const value = {
    ...state,
    login,
    logout,
    updateUser,
    dispatch
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthContext;
